<?phpheader('Content-Type: application/json');header('Access-Control-Allow-Origin: *');header('Access-Control-Allow-Methods: GET');header('Access-Control-Allow-Headers: Content-Type');try {    include 'DatabaseConfig.php';    $pdo = new PDO("mysql:host=" . DB_HOST . ";dbname=" . DB_NAME, DB_USER, DB_PASS);    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);    $stats = [];    $stmt = $pdo->query("SELECT COUNT(*) as count FROM users WHERE is_active = 1");    $stats['users'] = $stmt->fetch(PDO::FETCH_ASSOC)['count'];    $stmt = $pdo->query("SELECT COUNT(*) as count FROM products WHERE is_active = 1");    $stats['products'] = $stmt->fetch(PDO::FETCH_ASSOC)['count'];    $stmt = $pdo->query("SELECT COUNT(*) as count FROM suppliers WHERE is_active = 1");    $stats['suppliers'] = $stmt->fetch(PDO::FETCH_ASSOC)['count'];    $currentMonth = date('Y-m');    $stmt = $pdo->prepare("SELECT SUM(total_amount) as total FROM sales WHERE DATE_FORMAT(created_at, '%Y-%m') = ?");    $stmt->execute([$currentMonth]);    $result = $stmt->fetch(PDO::FETCH_ASSOC);    $stats['monthly_sales'] = floatval($result['total'] ?? 0);    $stmt = $pdo->prepare("SELECT COUNT(*) as count FROM sales WHERE DATE_FORMAT(created_at, '%Y-%m') = ?");    $stmt->execute([$currentMonth]);    $stats['monthly_transactions'] = $stmt->fetch(PDO::FETCH_ASSOC)['count'];    echo json_encode([        'success' => true,        'data' => $stats    ]);} catch(Exception $e) {    http_response_code(500);    echo json_encode([        'success' => false,        'message' => 'Database error: ' . $e->getMessage()    ]);}?>