<?phprequire_once __DIR__ . '/BaseAPI.php';require_once __DIR__ . '/SessionConfig.php';class LogoutAPI extends BaseAPI{    public function route(): void    {        $this->initializeSession();        $operation = $this->getOperation() ?: 'logoutUser';        $input = $this->getJsonInput() ?? [];        $this->handle(function () use ($operation, $input) {            switch ($operation) {                case 'logoutUser':                    $this->logoutUser($input);                    break;                default:                    $this->respondError('Unsupported operation', 400, ['operation' => $operation]);                    break;            }        });    }    public function logoutUser(array $input = []): void    {        destroySession();        $this->respond(['success' => true, 'message' => 'Logged out']);    }}if (!defined('TESTING') && basename($_SERVER['SCRIPT_NAME']) === 'LogoutAPI.php') {    $api = new LogoutAPI(['methods' => 'POST, OPTIONS']);    $api->route();}